$(window).on("load", function () {
    var emprestimosPendentes=$('#emprestimosPendentes').val();
    var emprestimosVigentes=$('#emprestimosVigentes').val();
    var emprestimosTransferidos=$('#emprestimosTransferidos').val();
    var emprestimosConcluidos=$('#emprestimosConcluidos').val();

    var a = $("#simple-pie-chart");
    new Chart(a, {
        type: "pie",
        options: {responsive: !0, maintainAspectRatio: !1, responsiveAnimationDuration: 500},
        data: {
            labels: ["Pendentes", "Vigentes", "Transferidos", "Concluidos"],
            datasets: [{
                label: "My First dataset",
                data: [emprestimosPendentes,emprestimosVigentes,emprestimosTransferidos,emprestimosConcluidos],
                backgroundColor: ["#FF4558", "#FF7D4D", "#00A5A8", "#28D094"]
            }]
        }
    })
});
