
$(window).on("load", function () {
    var prestacoesEmAtraso=$('#prestacoesEmAtraso').val();
    var prestacoesPorNotificar=$('#prestacoesPorNotificar').val();
    var prestacoesRegulares=$('#prestacoesRegulares').val();
    var prestacoesPagas=$('#prestacoesPagas').val();

    var a = $("#simple-doughnut-chart");
    new Chart(a, {
        type: "doughnut",
        options: {responsive: !0, maintainAspectRatio: !1, responsiveAnimationDuration: 500},
        data: {
            labels: ["Em Atraso", "Por Notificar", "Regulares", "Pagas"],
            datasets: [{
                label: "My First dataset",
                data: [prestacoesEmAtraso,prestacoesPorNotificar,prestacoesRegulares,prestacoesPagas],
                backgroundColor: ["#FF4558", "#FF7D4D", "#00A5A8", "#28D094"]
            }]
        }
    })
});